### File: Module 6 Constraintâ€¯Crunch.md ###

<!DOCTYPE html>
<html lang="en">
<head>
<meta charset="UTF-8"/>
<title>Constraintâ€¯Crunch!</title>
<style>
  /* ===== EMBEDDABLE LAYOUT (fits any iframe) ===== */
  body{margin:0;font-family:Arial,Helvetica,sans-serif;color:#333;line-height:1.5;}
  #game{max-width:800px;margin:0 auto;padding:16px;box-sizing:border-box;}
  h2{margin:4px 0 12px;color:hsl(359,97%,29%);} /* red heading */

  /* ===== METER BAR ===== */
  .meter{height:18px;background:#ddd;border:1px solid #555;position:relative;margin-bottom:6px;}
  .meter span{display:block;height:100%;background:#4caf50;width:0;transition:.2s;width:0;}
  .meter.over span{background:#f44336;}
  .meter-label{font-size:0.85em;margin-bottom:2px;display:flex;justify-content:space-between;}

  /* ===== REQUIREMENT CHIPS ===== */
  .req{padding:4px 6px;border:1px solid #555;font-size:0.82em;margin-bottom:4px;border-radius:4px;}
  .req.good{background:#4caf50;color:#fff;border:none;}
  .req.bad{background:#f44336;color:#fff;border:none;}

  /* ===== PART SELECT TABLE ===== */
  table{width:100%;border-collapse:collapse;margin-top:12px;}
  th,td{border:1px solid #aaa;padding:6px;text-align:center;font-size:0.9em;}
  th{background:#f0f0f0;}
  .part-row:hover{background:#fafafa;cursor:pointer;}
  .sel{outline:2px solid hsl(359,97%,29%);} /* selected part */

  /* ===== STATUS BOX ===== */
  #status{margin-top:10px;font-weight:bold;}

  /* ===== WIN/FAIL POPUP ===== */
  #popup{position:fixed;top:50%;left:50%;transform:translate(-50%,-50%);background:#fff;border:2px solid #333;padding:20px;display:none;z-index:99;max-width:80%;}
  #popup.good{border-color:#4caf50;} #popup.bad{border-color:#f44336;}
</style>
</head>
<body>
<div id="game">
  <h2>Robot Builder: Constraintâ€¯Crunch!</h2>
  <p><strong>Experiment time!</strong> Choose <strong>ONE</strong> option in each category (4 parts total).<br>
  â€¢ First, challenge yourself to keep the <em>Budget, Width, Noise, and Buildâ€‘Time</em> meters inside their green barsâ€”even if some requirement chips stay red.<br>
  â€¢ Next, tweak your build until every requirement chip flips green and watch how the meters react.<br>
  Where do the tradeâ€‘offs appear? Which parts pull you back inside the limits?</p>

  <!-- Constraint meters -->
  <div id="meters"></div>

  <!-- Requirement chips -->
  <div id="reqs"></div>

  <!-- Parts selection tables generated by JS -->
  <div id="catalog"></div>

  <div id="status"></div>
</div>

<div id="popup"></div>

<script>
// ====== DATA SETUP ======
const constraints=[
  {key:'budget',label:'Budget $',max:500,unit:'$',fmt:v=>'$'+v},
  {key:'width',label:'Max Width (cm)',max:60,unit:'cm',fmt:v=>v+'cm'},
  {key:'noise',label:'Noise (dB)',max:50,unit:'dB',fmt:v=>v+'dB'},
  {key:'time',label:'Build Time (weeks)',max:2,unit:'wk',fmt:v=>v+'wk'}
];
const requirements=[
  {key:'capacity',label:'Carry â‰¥ 20 trays',fn:stats=>stats.capacity>=20},
  {key:'ui',label:'Must include Touchscreen UI',fn:stats=>stats.ui}
];
// 4 basic parts, each with 3 variants (light, mid, heavy)
const parts=[
  {cat:'Chassis',key:'chassis',variants:[
    {name:'Lite Frame',cost:80,width:40,noise:0,time:0,capacity:5},
    {name:'Standard Frame',cost:120,width:50,noise:0,time:0,capacity:10},
    {name:'Heavy Frame',cost:200,width:60,noise:0,time:0,capacity:20}
  ]},
  {cat:'Arm',key:'arm',variants:[
    {name:'No Arm',cost:0,width:0,noise:0,time:0,capacity:0},
    {name:'Light Arm',cost:100,width:5,noise:5,time:0.5,capacity:5},
    {name:'Industrial Arm',cost:180,width:8,noise:10,time:1,capacity:10}
  ]},
  {cat:'Battery',key:'battery',variants:[
    {name:'Small Battery',cost:60,width:5,noise:0,time:0.3,capacity:0},
    {name:'Medium Battery',cost:120,width:7,noise:0,time:0.4,capacity:0},
    {name:'Large Battery',cost:160,width:8,noise:0,time:0.5,capacity:0}
  ]},
  {cat:'UI Panel',key:'ui',variants:[
    {name:'No UI',cost:0,width:0,noise:0,time:0,capacity:0,ui:false},
    {name:'Button Pad',cost:40,width:4,noise:0,time:0.2,capacity:0,ui:false},
    {name:'Touchscreen',cost:80,width:6,noise:0,time:0.3,capacity:0,ui:true}
  ]}
];

// current selections (index of variant for each part)
const selection={chassis:0,arm:0,battery:0,ui:0};

// ====== DOM BUILD ======
const meterBox=document.getElementById('meters');
const reqBox=document.getElementById('reqs');
const catBox=document.getElementById('catalog');
const status=document.getElementById('status');
const popup=document.getElementById('popup');

function buildUI(){
  // meters
  meterBox.innerHTML='';
  constraints.forEach(c=>{
    meterBox.insertAdjacentHTML('beforeend',`<div class="meter-label"><span>${c.label}</span><span id="val-${c.key}"></span></div><div id="bar-${c.key}" class="meter"><span></span></div>`);
  });
  // requirements chips
  reqBox.innerHTML='<h3>Requirements</h3>';
  requirements.forEach((r,i)=>{
    reqBox.insertAdjacentHTML('beforeend',`<div id="rq-${i}" class="req">${r.label}</div>`);
  });
  // parts tables
  catBox.innerHTML='';
  parts.forEach(part=>{
    let html=`<h3>${part.cat}</h3><table><tr><th>Option</th><th>$</th><th>W(cm)</th><th>dB</th><th>Weeks</th><th>Carry</th></tr>`;
    part.variants.forEach((v,i)=>{
      html+=`<tr class="part-row" data-cat="${part.key}" data-idx="${i}"><td>${v.name}</td><td>${v.cost}</td><td>${v.width}</td><td>${v.noise}</td><td>${v.time}</td><td>${v.capacity||''}</td></tr>`;
    });
    html+='</table>';
    catBox.insertAdjacentHTML('beforeend',html);
  });
  // click handlers
  document.querySelectorAll('.part-row').forEach(row=>{
    row.addEventListener('click',()=>{
      selection[row.dataset.cat]=+row.dataset.idx;
      update();
    });
  });
}

// ====== GAME LOGIC ======
function computeStats(){
  const stats={cost:0,width:0,noise:0,time:0,capacity:0,ui:false};
  parts.forEach(p=>{
    const v=p.variants[selection[p.key]];
    stats.cost += v.cost;
    stats.width += v.width;
    stats.noise += v.noise;
    stats.time += v.time;
    stats.capacity += v.capacity;
    if(v.ui) stats.ui=true;
  });
  return stats;
}

function update(){
  // highlight selected rows
  document.querySelectorAll('.part-row').forEach(r=>r.classList.remove('sel'));
  parts.forEach(p=>{
    const idx=selection[p.key];
    document.querySelector(`.part-row[data-cat="${p.key}"][data-idx="${idx}"]`).classList.add('sel');
  });
  const s=computeStats();
  // update meters
  constraints.forEach(c=>{
    const val=c.key==='budget'?s.cost: c.key==='width'?s.width: c.key==='noise'?s.noise: s.time;
    const pct=Math.min(100,val/c.max*100);
    const bar=document.querySelector(`#bar-${c.key}`);
    bar.querySelector('span').style.width=pct+'%';
    bar.classList.toggle('over',val>c.max);
    document.getElementById(`val-${c.key}`).textContent=c.fmt(val)+' / '+c.fmt(c.max);
  });
  // requirements
  requirements.forEach((r,i)=>{
    const good=r.fn(s);
    const chip=document.getElementById(`rq-${i}`);
    chip.className='req '+(good?'good':'bad');
  });
  // win/lose check
  const anyOver=document.querySelectorAll('.meter.over').length>0;
  const anyBad=document.querySelectorAll('.req.bad').length>0;
  status.textContent= anyOver || anyBad? 'Constraints unmet â€“ keep tweaking!' : 'âœ” All constraints satisfied!';
  if(!anyOver && !anyBad){
    endRound(true);
  }
}

function endRound(success){
  popup.className= success? 'good':'bad';
  popup.textContent= success? 'ðŸŽ‰ Perfect build! You stayed inside every constraint and met all requirements.' : 'âŒ Constraints violated!';
  popup.style.display='block';
  setTimeout(()=>popup.style.display='none',3000);
}

buildUI();
update();
</script>
</body>
</html>
